import postcss from 'postcss';
import postcssNodeExtract from './lib/postcss-node-extract';

/**
 * Default options.
 *
 * @type {Object}
 */
const defaultOptions = {
  css: ``,
  filterNames: [],
  customFilter: undefined,
  postcssSyntax: undefined,
};

/**
 * CssNodeExtract
 */
export default class CssNodeExtract {
  /**
   * Asynchronously extract nodes from a string.
   *
   * @param {Object} options
   *   Configuration options.
   * @return {Promise}
   *   Promise for a string with the extracted nodes.
   */
  static process(options = {}) {
    return new Promise((resolve) => {
      const result = CssNodeExtract.processSync(options);
      resolve(result);
    });
  }

  /**
   * Synchronously extract nodes from a string.
   *
   * @param {Object} options
   *   Configuration options.
   * @return {String}
   *   Extracted nodes.
   */
  static processSync(options = {}) {
    const data = Object.assign({}, defaultOptions, options);
    return postcss(postcssNodeExtract(data.filterNames, data.customFilter))
      .process(data.css, { syntax: data.postcssSyntax }).css;
  }
}

import nodeMatchesFilter from './node-matches-filter';

/**
 * Whiteliste a node if it (or one of the nodes parents) matches the given filter.
 *
 * @param {Object} node
 *   A postcss node object.
 * @param {Array} filterGroups
 *   Array of filter groups.
 * @return {Boolean}
 *   Returns true if the node (or one of its parents) matches one or more
 *   filter groups and false if not.
 */
export default function extractNodeRecursively(node, filterGroups) {
  if (node.parent && node.parent.type !== `root`) return extractNodeRecursively(node.parent, filterGroups);

  let extractNode = false;

  filterGroups.some((groupOrFilter) => {
    const filterGroup = Array.isArray(groupOrFilter) ? groupOrFilter : [groupOrFilter];
    extractNode = filterGroup.filter(
      filter => !nodeMatchesFilter(node, filter)
    ).length === 0;
    return extractNode;
  });

  return extractNode;
}

/**
 * Filter definitions.
 *
 * @type {Object}
 */
const filterDefinitions = {
  'at-rules': [
    { property: `type`, value: `atrule` },
  ],
  declarations: [
    { property: `type`, value: `decl` },
  ],
  functions: [
    [
      { property: `type`, value: `atrule` },
      { property: `name`, value: `function` },
    ],
  ],
  mixins: [
    [
      { property: `type`, value: `atrule` },
      { property: `name`, value: `mixin` },
    ],
    [
      { property: `type`, value: `rule` },
      { property: `selector`, value: /\(.*\)/ },
    ],
  ],
  rules: [
    { property: `type`, value: `rule` },
  ],
  silent: [
    [
      { property: `type`, value: `atrule` },
      { property: `name`, value: `debug` },
    ],
    [
      { property: `type`, value: `atrule` },
      { property: `name`, value: `error` },
    ],
    [
      { property: `type`, value: `atrule` },
      { property: `name`, value: `function` },
    ],
    [
      { property: `type`, value: `atrule` },
      { property: `name`, value: `mixin` },
    ],
    [
      { property: `type`, value: `atrule` },
      { property: `name`, value: `warn` },
    ],
    [
      { property: `type`, value: `decl` },
      { property: `prop`, value: /^[$|@]/ },
    ],
    [
      { property: `type`, value: `rule` },
      { property: `selector`, value: /%/ },
    ],
    [
      { property: `type`, value: `rule` },
      { property: `selector`, value: /\(.*\)/ },
    ],
  ],
  variables: [
    [
      { property: `type`, value: `decl` },
      { property: `prop`, value: /^[$|@]/ },
    ],
  ],
};

export default filterDefinitions;

/**
 * Check if a node matches the given filter.
 *
 * @param {Object} node
 *   A postcss node object.
 * @param {Object} filter
 *   Filter object.
 * @return {Boolean}
 *   Returns true if the node matches the filter and false if not.
 */
export default function nodeMatchesFilter(node, filter) {
  if (!node[filter.property]) return false;
  if (node[filter.property] === filter.value) return true;
  if (filter.value instanceof RegExp && filter.value.test(node[filter.property])) return true;
  return false;
}

import postcss from 'postcss';
import extractNodeRecursively from './extract-node-recursively';
import filterDefinitions from './filter-definitions';

/**
 * A PostCSS plugin for extracting nodes from CSS code.
 *
 * @param {Array|String} filterNames
 *   Multiple filter names as array or a single filter name as string.
 * @param {Object} customFilter
 *   Custom filter object.
 * @return {Function}
 *   PostCSS plugin.
 */
export default function postcssNodeExtract(filterNames = [], customFilter) {
  const filterNamesArray = Array.isArray(filterNames) ? filterNames : [filterNames];
  filterDefinitions.custom = customFilter;

  return postcss.plugin(`postcss-node-extract`, () => (nodes) => {
    nodes.walk((rule) => {
      let filterRule = false;
      filterNamesArray.some((filterName) => {
        filterRule = extractNodeRecursively(rule, filterDefinitions[filterName]);
        return filterRule;
      });
      if (!filterRule) rule.remove();
    });
  });
}

