  const scss = fs.readFileSync(`test/css/test.scss`, { encoding: `utf8` });
  const less = fs.readFileSync(`test/css/test.less`, { encoding: `utf8` });

  it(`should be a function`, () => {
    expect(typeof CssNodeExtract).to.equal(`function`);
