  describe(`process()`, () => {
    it(`should be a function`, () => {
      expect(typeof CssNodeExtract.process).to.equal(`function`);
    });

    it(`SCSS: should extract only at-rules`, () => {
      const reference = fs.readFileSync(`test/css/reference/at-rules.scss`, {
        encoding: `utf8`,
      });
